package library.lanshifu.com.myapplication.model;import org.litepal.crud.DataSupport;public class MusicInfo extends DataSupport{	private int id;	private String filepath;	private String filename;	private String fileSize;	public MusicInfo(String filepath, String filename, String fileSize) {		this.filepath = filepath;		this.filename = filename;		this.fileSize = fileSize;	}	public MusicInfo() {	}	public String getFilepath() {		return this.filepath;	}	public void setFilepath(String filepath) {		this.filepath = filepath;	}	public String getFilename() {		return this.filename;	}	public void setFilename(String filename) {		this.filename = filename;	}	public int getId() {		return this.id;	}	public void setId(int id) {		this.id = id;	}	public String getFileSize() {		return this.fileSize;	}	public void setFileSize(String fileSize) {		this.fileSize = fileSize;	}}